import React, { Component } from 'react'
import { Header, Transition } from 'semantic-ui-react'

import { Arepa } from '@components/SVGIcons'

export default class gfg extends React.Component<{}, {value: string}> {
  constructor(props: any) {
    super(props);

    this.state = {value: ''};
  }

  handleChange = (event: any) => {
    this.setState({value: event.target.value});
  };
  gfg1 = () =>
    this.setState((btt) => ({ visible: !btt.visible }))

  render() {
    const { visible } = this.state

    return (
      <div>
        <Button
          content={visible ? 'Click here' : 'Click here'}
          onClick={this.gfg1}
        />
        <br/>
        <Transition visible={visible} animation='jiggle' duration={250}>
          <Icon size='huge' name='js' />
        </Transition>
      </div>
    )
  }
}


type AnimatedHeaderProps = {
  visible: boolean
  onClick: () => void
  onComplete: () => void
}
const AnimatedHeader = ({
  visible,
  onClick,
  onComplete,
}: AnimatedHeaderProps) => (
  <Header size="huge" as="h1" onClick={onClick}>
    Are
    <Transition
      animation="jiggle"
      visible={visible}
      duration={900}
      onComplete={onComplete}
    >
      <Arepa size="58px" />
    </Transition>
    Passion
  </Header>
)
