import React, { Component } from 'react'
import { Header, Transition } from 'semantic-ui-react'

import { Arepa } from '@components/SVGIcons'

export default class gfg extends React.Component<{}, {
  visible: boolean, 
  onClick: () => void,
  onComplete:() => void}> {
  constructor(props: any) {
    super(props);

    this.state = {visible: true, onClick: this.gfg1, onComplete: on};
  }

  gfg1 = () =>
    this.setState((btt) => ({ visible: !btt.visible }))

  render() {
    const { visible } = this.state

    return (
      <Header size="huge" as="h1" onClick={this.gfg1}>
    Are
    <Transition
      animation="jiggle"
      visible={visible}
      duration={900}
      onComplete={onComplete}
    >
      <Arepa size="58px" />
    </Transition>
    Passion
  </Header>
    )
  }
}


type AnimatedHeaderProps = {
  visible: boolean
  onClick: () => void
  onComplete: () => void
}
const AnimatedHeader = ({
  visible,
  onClick,
  onComplete,
}: AnimatedHeaderProps) => (
  <Header size="huge" as="h1" onClick={onClick}>
    Are
    <Transition
      animation="jiggle"
      visible={visible}
      duration={900}
      onComplete={onComplete}
    >
      <Arepa size="58px" />
    </Transition>
    Passion
  </Header>
)
