import React, { Component } from 'react'
import { Header, Transition } from 'semantic-ui-react'

import { Arepa } from '@components/SVGIcons'

const transitions = [
  'jiggle',
  'flash',
  'shake',
  'pulse',
  'tada',
  'bounce',
  'glow',
]

const options = transitions.map((name: string) => ({
  key: name,
  text: name,
  value: name,
}))

export default class TransitionExampleTransitionExplorer extends Component {
  state = { animation: transitions[0], duration: 500, visible: true }

handleChange = (e, { name, value }) => this.setState({ [name]: value })
toggleVisibility = () =>
this.setState((prevState) => ({ visible: !prevState.visible }))

render() {
  const { animation, duration, visible } = this.state

  return (
    <Header size="huge" as="h1" onClick={onClick}>
    Are
    <Transition
      animation="jiggle"
      visible={visible}
      duration={900}
      onComplete={onComplete}
    >
      <Arepa size="58px" />
    </Transition>
    Passion
  </Header>

  )
}
}


type AnimatedHeaderProps = {
  visible: boolean
  onClick: () => void
  onComplete: () => void
}
const AnimatedHeader = ({
  visible,
  onClick,
  onComplete,
}: AnimatedHeaderProps) => (
  <Header size="huge" as="h1" onClick={onClick}>
    Are
    <Transition
      animation="jiggle"
      visible={visible}
      duration={900}
      onComplete={onComplete}
    >
      <Arepa size="58px" />
    </Transition>
    Passion
  </Header>
)
